'use strict';
const lessImport = require('../');
const assert = require('power-assert');

describe('Error test', function() {
    it('lessImport without path', function(done) {
        try {
            lessImport();
        }
        catch(err) {
            assert(err.message === 'You must set "path" with path.join');
            return done();
        }
        done(new Error('无效的执行点'));
    });
});