'use strict';
const assert = require('power-assert');
const GulpMap = require('../');
const map = new GulpMap();

describe('Error Test', function() {
    it('savePath', function(done) {
        try {
            map.savePath({
                replace: 'css',
                to: ''
            });
        }
        catch(err) {
            assert(err.message === 'You must both set "replace" & "to" by calling "savePath"');
            return done();
        }
        done(new Error('无效的执行点'));
    });
    it('buildMap', function(done) {
        try {
            map.buildMap();
        }
        catch(err) {
            assert(err.message === 'You must set "buildDir" & "to" by calling "buildMap"');
            return done();
        }
        done(new Error('无效的执行点'));
    });
});